/**
 * Tema retro de 16 bits para Cybervania
 * Diseño minimalista que no depende de recursos externos
 */

:root {
    /* Paleta de colores estilo SNES/NES */
    --retro-bg: #1a1c2c;
    --retro-dark-purple: #2d1b2e;
    --retro-purple: #5d2e46;
    --retro-red: #b13e53;
    --retro-orange: #ef7d57;
    --retro-yellow: #ffcd75;
    --retro-lime: #a7f070;
    --retro-green: #38b764;
    --retro-blue: #3b5dc9;
    --retro-dark-blue: #29366f;
    --retro-gray: #566c86;
    --retro-dark-gray: #333c57;
    --retro-white: #f4f4f4;
    
    /* Elementos de UI */
    --pixel-size: 4px;
    --border-width: 4px;
    --menu-frame-color: var(--retro-gray);
    --menu-bg-color: var(--retro-dark-blue);
    --menu-text-color: var(--retro-white);
    --highlight-color: var(--retro-yellow);
}

/* Ajuste global para que todo tenga aspecto pixelado */
html {
    image-rendering: pixelated;
    image-rendering: crisp-edges;
    -webkit-font-smoothing: none;
    font-smooth: never;
}

body {
    margin: 0;
    padding: 0;
    background-color: var(--retro-bg);
    color: var(--menu-text-color);
    font-family: 'Press Start 2P', monospace;
    line-height: 1.5;
    overflow-x: hidden;
}

/* Contenedor principal con patrón de fondo pixelado */
#menu-container {
    position: relative;
    width: 100%;
    height: 100vh;
    /* Patrón pixelado simple que no requiere imagen externa */
    background-color: var(--retro-bg);
    background-image: 
        linear-gradient(rgba(45, 27, 46, 0.3) 1px, transparent 1px),
        linear-gradient(90deg, rgba(45, 27, 46, 0.3) 1px, transparent 1px);
    background-size: 16px 16px;
    overflow: hidden;
}

/* ===== TÍTULO ===== */
.title-container {
    position: absolute;
    top: 10%;
    left: 50%;
    transform: translateX(-50%);
    text-align: center;
    width: 90%;
}

.title-container h1 {
    font-size: 48px;
    line-height: 1.2;
    color: var(--retro-yellow);
    text-shadow: 
        calc(var(--pixel-size) / 2) calc(var(--pixel-size) / 2) 0 var(--retro-red),
        var(--pixel-size) var(--pixel-size) 0 var(--retro-dark-blue);
    margin: 0 0 20px 0;
    text-transform: uppercase;
    animation: retro-pulse 2s infinite alternate;
}

.title-container h2 {
    font-size: 16px;
    color: var(--retro-white);
    margin: 0;
    line-height: 1.5;
    text-shadow: var(--pixel-size) var(--pixel-size) 0 var(--retro-dark-purple);
}

@keyframes retro-pulse {
    0% { text-shadow: var(--pixel-size) var(--pixel-size) 0 var(--retro-dark-purple); }
    100% { text-shadow: var(--pixel-size) var(--pixel-size) 0 var(--retro-red); }
}

/* ===== PANTALLAS DE MENÚ ===== */
.menu-screen {
    background-color: var(--menu-bg-color);
    border: var(--border-width) solid var(--menu-frame-color);
    width: 700px;
    max-width: 90%;
    padding: 20px;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -40%);
    box-shadow: 
        var(--pixel-size) var(--pixel-size) 0 var(--retro-dark-gray),
        calc(var(--pixel-size) * 2) calc(var(--pixel-size) * 2) 0 rgba(0, 0, 0, 0.3);
}

.menu-screen h3 {
    font-size: 24px;
    color: var(--highlight-color);
    text-align: center;
    margin: 0 0 20px 0;
    padding-bottom: 20px;
    border-bottom: var(--pixel-size) solid var(--retro-dark-gray);
}

/* ===== ELEMENTOS DE MENÚ ===== */
.menu-list {
    list-style: none;
    padding: 0;
    margin: 30px 0;
}

.menu-item {
    background-color: var(--retro-dark-gray);
    border: var(--border-width) solid var(--menu-frame-color);
    padding: 15px;
    margin: 10px 0;
    cursor: pointer;
    display: flex;
    align-items: center;
    position: relative;
}

.menu-item:hover {
    background-color: var(--retro-purple);
    border-color: var(--highlight-color);
}

.menu-item::before {
    content: "►";
    color: var(--highlight-color);
    opacity: 0;
    position: absolute;
    left: -25px;
}

.menu-item:hover::before {
    opacity: 1;
}

.menu-text {
    color: var(--menu-text-color);
    font-size: 16px;
    font-family: 'Press Start 2P', monospace;
}

/* ===== BOTONES ===== */
button {
    background-color: var(--retro-dark-gray);
    color: var(--menu-text-color);
    border: var(--border-width) solid var(--menu-frame-color);
    padding: 10px 20px;
    font-family: 'Press Start 2P', monospace;
    font-size: 14px;
    cursor: pointer;
    position: relative;
    box-shadow: var(--pixel-size) var(--pixel-size) 0 rgba(0, 0, 0, 0.3);
}

button:hover {
    background-color: var(--retro-purple);
    border-color: var(--highlight-color);
}

button:active {
    transform: translate(var(--pixel-size), var(--pixel-size));
    box-shadow: none;
}

.main-button {
    background-color: var(--retro-red);
    border-color: var(--retro-yellow);
}

.back-button {
    background-color: var(--retro-dark-blue);
    border-color: var(--retro-blue);
}

/* ===== FORMULARIOS ===== */
input, select, textarea {
    background-color: var(--retro-dark-gray);
    border: var(--border-width) solid var(--menu-frame-color);
    color: var(--menu-text-color);
    padding: 10px;
    font-family: 'Press Start 2P', monospace;
    font-size: 12px;
    width: 100%;
}

input:focus, select:focus, textarea:focus {
    border-color: var(--highlight-color);
    outline: none;
}

/* ===== TARJETAS DE ESPECIALIZACIÓN ===== */
.spec-card {
    background-color: var(--retro-dark-gray);
    border: var(--border-width) solid var(--menu-frame-color);
    padding: 15px;
    margin-bottom: 15px;
    position: relative;
}

.spec-card.red { border-color: var(--retro-red); }
.spec-card.blue { border-color: var(--retro-blue); }
.spec-card.purple { border-color: var(--retro-purple); }

.spec-card.selected {
    border-color: var(--highlight-color);
    background-color: var(--retro-purple);
}

/* ===== NOTIFICACIONES ===== */
.notification {
    background-color: var(--retro-dark-gray);
    border: var(--border-width) solid var(--highlight-color);
    color: var(--menu-text-color);
    padding: 15px;
    position: fixed;
    bottom: 20px;
    left: 50%;
    transform: translateX(-50%);
    z-index: 100;
    font-family: 'Press Start 2P', monospace;
    font-size: 12px;
    min-width: 300px;
    text-align: center;
    box-shadow: var(--pixel-size) var(--pixel-size) 0 rgba(0, 0, 0, 0.5);
}

/* ===== ANIMACIONES ===== */
@keyframes menu-entrance {
    from {
        opacity: 0;
        transform: translate(-50%, -30%);
    }
    to {
        opacity: 1;
        transform: translate(-50%, -40%);
    }
}

.menu-screen.active {
    animation: menu-entrance 0.3s forwards;
}

/* Añadimos píxeles animados para el fondo */
.pixel-background {
    position: absolute;
    width: 100%;
    height: 100%;
    top: 0;
    left: 0;
    overflow: hidden;
    z-index: 0;
}

.pixel {
    position: absolute;
    width: 8px;
    height: 8px;
    background-color: var(--retro-blue);
    opacity: 0.3;
}
