/**
 * SNES-style 16-bit theme for Cybervania
 * Inspirado en juegos como Castlevania, Super Metroid y Final Fantasy
 */

@font-face {
    font-family: 'PressStart2P';
    src: url('../assets/fonts/PressStart2P-Regular.woff2') format('woff2');
    font-weight: normal;
    font-style: normal;
    font-display: swap;
}

:root {
    /* Paleta de colores estilo SNES */
    --snes-dark-purple: #2d1b2e;
    --snes-purple: #5d2e46;
    --snes-red: #b13e53;
    --snes-orange: #ef7d57;
    --snes-yellow: #ffcd75;
    --snes-lime: #a7f070;
    --snes-green: #38b764;
    --snes-dark-green: #257179;
    --snes-dark-blue: #29366f;
    --snes-blue: #3b5dc9;
    --snes-light-blue: #41a6f6;
    --snes-sky: #73eff7;
    --snes-black: #1a1c2c;
    --snes-dark-gray: #333c57;
    --snes-medium-gray: #566c86;
    --snes-light-gray: #94b0c2;
    --snes-white: #f4f4f4;
    
    /* Elementos principales */
    --border-width: 4px;
    --pixel-size: 4px;
    --menu-frame-color: var(--snes-medium-gray);
    --menu-bg-color: var(--snes-dark-blue);
    --menu-bg-highlight: var(--snes-purple);
    --menu-text-color: var(--snes-white);
    --highlight-color: var(--snes-yellow);
    --accent-color: var(--snes-red);
    
    /* Color para cada especialización */
    --red-team-color: var(--snes-red);
    --blue-team-color: var(--snes-blue);
    --purple-team-color: var(--snes-purple);
}

/* Estilo global pixelado */
body {
    font-family: 'PressStart2P', monospace;
    background-color: var(--snes-black);
    color: var(--menu-text-color);
    line-height: 1.5;
    image-rendering: pixelated;
    image-rendering: crisp-edges;
    font-smooth: never;
    -webkit-font-smoothing: none;
    letter-spacing: 1px;
}

/* Contenedor del menú con borde pixelado */
#menu-container {
    position: relative;
    width: 100%;
    height: 100vh;
    background-image: url('../assets/images/backgrounds/pixel_castle_bg.png'); 
    background-size: 512px 512px; /* Mantener el pixelado */
    background-repeat: repeat;
}

/* Título con efecto pixelado */
.title-container {
    transform: translateZ(0); /* Para forzar la renderización pixelada */
}

.title-container h1 {
    font-family: 'PressStart2P', monospace;
    font-size: 48px;
    color: var(--snes-white);
    text-transform: uppercase;
    letter-spacing: 2px;
    text-shadow: 
        var(--pixel-size) 0 0 var(--snes-purple),
        0 var(--pixel-size) 0 var(--snes-dark-blue),
        var(--pixel-size) var(--pixel-size) 0 var(--snes-dark-purple);
    margin-bottom: 20px;
    animation: snes-title-flash 2s infinite alternate;
}

.title-container h2 {
    font-family: 'PressStart2P', monospace;
    font-size: 16px;
    color: var(--snes-yellow);
    margin-top: 10px;
}

/* Pantallas del menú con borde pixelado */
.menu-screen {
    background-color: var(--menu-bg-color);
    border: var(--border-width) solid var(--menu-frame-color);
    padding: 24px;
    width: 700px;
    max-width: 90%;
    box-shadow: 
        var(--pixel-size) var(--pixel-size) 0 var(--snes-dark-gray),
        inset var(--pixel-size) var(--pixel-size) 0 rgba(255, 255, 255, 0.1),
        inset calc(var(--pixel-size) * -1) calc(var(--pixel-size) * -1) 0 rgba(0, 0, 0, 0.2);
    position: relative;
}

/* Cabecera de las pantallas */
.menu-screen h3 {
    font-family: 'PressStart2P', monospace;
    font-size: 24px;
    color: var(--highlight-color);
    text-align: center;
    margin: 0 0 20px;
    padding-bottom: 16px;
    border-bottom: var(--pixel-size) solid var(--snes-dark-gray);
    text-transform: uppercase;
    letter-spacing: 2px;
}

/* Lista del menú principal con estilo SNES */
.menu-list {
    list-style: none;
    padding: 0;
    margin: 20px 0;
}

.menu-item {
    padding: 12px 16px;
    margin: 10px 0;
    background-color: var(--snes-dark-gray);
    border: var(--pixel-size) solid var(--snes-medium-gray);
    cursor: pointer;
    display: flex;
    align-items: center;
    position: relative;
}

.menu-item:hover, .menu-item:focus {
    background-color: var(--menu-bg-highlight);
    border-color: var(--highlight-color);
}

.menu-item:active {
    transform: translate(var(--pixel-size), var(--pixel-size));
}

.menu-item::before {
    content: "►";
    position: absolute;
    left: -24px;
    color: var(--highlight-color);
    opacity: 0;
    transition: opacity 0.1s;
}

.menu-item:hover::before {
    opacity: 1;
}

.menu-icon {
    margin-right: 16px;
    width: 24px;
    height: 24px;
    text-align: center;
    color: var(--highlight-color);
    font-size: 16px;
}

.menu-text {
    font-family: 'PressStart2P', monospace;
    font-size: 16px;
    color: var(--snes-white);
}

/* Botones con estilo de 16 bits */
button {
    background-color: var(--snes-dark-gray);
    color: var(--menu-text-color);
    border: var(--pixel-size) solid var(--menu-frame-color);
    padding: 12px 16px;
    font-family: 'PressStart2P', monospace;
    font-size: 14px;
    cursor: pointer;
    text-transform: uppercase;
    min-width: 160px;
    box-shadow: var(--pixel-size) var(--pixel-size) 0 rgba(0, 0, 0, 0.3);
}

button:hover {
    background-color: var(--menu-bg-highlight);
    border-color: var(--highlight-color);
}

button:active {
    transform: translate(var(--pixel-size), var(--pixel-size));
    box-shadow: none;
}

.main-button {
    background-color: var(--snes-red);
    border-color: var(--snes-yellow);
}

.main-button:hover {
    background-color: var(--snes-orange);
}

.back-button {
    background-color: var(--snes-dark-blue);
    border-color: var(--snes-light-blue);
}

.back-button:hover {
    background-color: var(--snes-blue);
}

/* Tarjetas de especialización */
.spec-card {
    background-color: var(--snes-dark-gray);
    border: var(--pixel-size) solid var(--menu-frame-color);
    padding: 16px;
    margin-bottom: 16px;
    box-shadow: var(--pixel-size) var(--pixel-size) 0 rgba(0, 0, 0, 0.2);
}

.spec-card.red {
    border-color: var(--red-team-color);
}

.spec-card.blue {
    border-color: var(--blue-team-color);
}

.spec-card.purple {
    border-color: var(--purple-team-color);
}

.spec-card.selected {
    background-color: var(--menu-bg-highlight);
    border-color: var(--highlight-color);
    transform: translate(calc(var(--pixel-size) * -1), calc(var(--pixel-size) * -1));
    box-shadow: calc(var(--pixel-size) * 2) calc(var(--pixel-size) * 2) 0 rgba(0, 0, 0, 0.2);
}

.spec-card h5 {
    font-family: 'PressStart2P', monospace;
    font-size: 18px;
    color: var(--highlight-color);
    margin: 0 0 12px;
    text-transform: uppercase;
}

/* Inputs con estilo 16 bits */
input[type="text"], 
input[type="range"], 
textarea, 
select {
    background-color: var(--snes-dark-gray);
    border: var(--pixel-size) solid var(--menu-frame-color);
    color: var(--menu-text-color);
    padding: 12px;
    font-family: 'PressStart2P', monospace;
    font-size: 14px;
}

input[type="text"]:focus, 
textarea:focus, 
select:focus {
    border-color: var(--highlight-color);
    outline: none;
}

/* Sliders personalizados */
input[type="range"] {
    -webkit-appearance: none;
    height: 16px;
    background: var(--snes-dark-blue);
    outline: none;
}

input[type="range"]::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    width: 16px;
    height: 24px;
    background: var(--highlight-color);
    cursor: pointer;
    border: none;
}

input[type="range"]::-moz-range-thumb {
    width: 16px;
    height: 24px;
    background: var(--highlight-color);
    cursor: pointer;
    border: none;
}

/* Secciones de opciones */
.options-section {
    background-color: var(--snes-dark-blue);
    border: var(--pixel-size) solid var(--menu-frame-color);
    margin-bottom: 20px;
    padding: 16px;
}

.options-section h4 {
    font-family: 'PressStart2P', monospace;
    font-size: 16px;
    color: var(--highlight-color);
    margin: -16px -16px 16px;
    padding: 12px 16px;
    background-color: var(--snes-dark-gray);
    text-transform: uppercase;
}

/* Toggle switches */
.toggle-wrapper {
    position: relative;
    display: inline-block;
    width: 64px;
    height: 32px;
}

.toggle-wrapper input {
    opacity: 0;
    width: 0;
    height: 0;
}

.toggle-display {
    position: absolute;
    cursor: pointer;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-color: var(--snes-dark-gray);
    border: var(--pixel-size) solid var(--menu-frame-color);
}

.toggle-display:before {
    position: absolute;
    content: "";
    height: 16px;
    width: 16px;
    left: 4px;
    bottom: 4px;
    background-color: var(--snes-light-gray);
}

input:checked + .toggle-display {
    background-color: var(--snes-green);
}

input:checked + .toggle-display:before {
    transform: translateX(32px);
    background-color: var(--snes-yellow);
}

/* Vista previa del personaje */
.character-avatar {
    width: 110px;
    height: 150px;
    background-color: var(--snes-dark-gray);
    border: var(--pixel-size) solid var(--menu-frame-color);
    box-shadow: var(--pixel-size) var(--pixel-size) 0 rgba(0, 0, 0, 0.2);
}

.preview-name {
    font-family: 'PressStart2P', monospace;
    font-size: 16px;
    color: var(--highlight-color);
}

/* Notificaciones */
.notification {
    background-color: var(--snes-dark-gray);
    border: var(--pixel-size) solid var(--snes-yellow);
    color: var(--snes-white);
    padding: 16px;
    font-family: 'PressStart2P', monospace;
    font-size: 14px;
    box-shadow: var(--pixel-size) var(--pixel-size) 0 rgba(0, 0, 0, 0.3);
}

/* Animaciones */
@keyframes snes-title-flash {
    0% {
        color: var(--snes-white);
    }
    50% {
        color: var(--highlight-color);
    }
    100% {
        color: var(--snes-white);
    }
}

@keyframes pixel-entrance {
    0% {
        opacity: 0;
        transform: scale(0.9);
    }
    30% {
        opacity: 1;
    }
    70% {
        transform: scale(1.05);
    }
    100% {
        transform: scale(1);
    }
}

/* Scroll personalizado */
::-webkit-scrollbar {
    width: 12px;
}

::-webkit-scrollbar-track {
    background: var(--snes-dark-gray);
    border-left: var(--pixel-size) solid var(--menu-frame-color);
}

::-webkit-scrollbar-thumb {
    background: var(--snes-medium-gray);
    border-left: var(--pixel-size) solid var(--menu-frame-color);
}

::-webkit-scrollbar-thumb:hover {
    background: var(--highlight-color);
}

/* Fondo para los elementos especiales */
.pixelated-bg {
    background-image: url('../assets/images/backgrounds/pixel_pattern.png');
    background-size: 32px 32px; /* Mantener pixelado */
    background-repeat: repeat;
}

/* Estilo para estadísticas */
.stat-item {
    display: flex;
    justify-content: space-between;
    margin-bottom: 8px;
    font-family: 'PressStart2P', monospace;
    font-size: 12px;
}

.stat-value {
    color: var(--highlight-color);
}

.stat-positive {
    color: var(--snes-lime);
}

.stat-negative {
    color: var(--snes-red);
}

/* Selección de género */
.gender-option {
    background-color: var(--snes-dark-gray);
    border: var(--pixel-size) solid var(--menu-frame-color);
    padding: 16px;
    transition: none;
}

.gender-option.selected {
    background-color: var(--menu-bg-highlight);
    border-color: var(--highlight-color);
    transform: translate(calc(var(--pixel-size) * -1), calc(var(--pixel-size) * -1));
    box-shadow: calc(var(--pixel-size) * 2) calc(var(--pixel-size) * 2) 0 rgba(0, 0, 0, 0.2);
}

/* Modo de alto contraste */
.high-contrast {
    --snes-dark-blue: #000066;
    --snes-dark-gray: #333333;
    --highlight-color: #ffff00;
    --menu-text-color: #ffffff;
    --border-width: 4px;
}
