+    startAmbientLightning() {
+        // Efecto de relámpagos ambientales ocasionales
+        const flashLightning = () => {
+            // Probabilidad baja de relámpago
+            if (Math.random() < 0.1) {
+                const lightning = document.createElement('div');
+                lightning.className = 'ambient-lightning';
+                this.container.appendChild(lightning);
+                
+                // Activar el destello
+                setTimeout(() => {
+                    lightning.classList.add('flash');
+                    
+                    // Sonido sutil de trueno (implementado como console.log)
+                    console.log('Thunder sound: distant rumble');
+                    
+                    // Eliminar después de animación
+                    setTimeout(() => {
+                        lightning.remove();
+                    }, 1500);
+                }, 100);
+            }
+            
+            // Programar próximo relámpago
+            const nextTime = Math.random() * 20000 + 10000; // 10-30 segundos
+            setTimeout(flashLightning, nextTime);
+        };
+        
+        // Iniciar ciclo
+        const initialDelay = Math.random() * 10000 + 5000;
+        setTimeout(flashLightning, initialDelay);
+    }
+    
+    // Método para aplicar efectos adicionales a textos específicos
+    applyBloodyText(selector) {
+        const elements = document.querySelectorAll(selector);
+        elements.forEach(element => {
+            element.classList.add('bloody-text');
+            
+            // Añadir gotas de sangre digitales
+            const dropCount = Math.floor(Math.random() * 3) + 1;
+            for (let i = 0; i < dropCount; i++) {
+                const drop = document.createElement('span');
+                drop.className = 'blood-drop';
+                drop.style.left = `${Math.random() * 80 + 10}%`;
+                drop.style.animationDelay = `${Math.random() * 2}s`;
+                element.appendChild(drop);
+            }
+        });
+    }
+    
+    // Aplicar efecto de distorsión VHS a elementos
+    applyVHSDistortion(selector) {
+        const elements = document.querySelectorAll(selector);
+        elements.forEach(element => {
+            element.classList.add('vhs-distortion');
+            
+            // Crear capas de distorsión
+            const noiseLayer = document.createElement('div');
+            noiseLayer.className = 'vhs-noise';
+            
+            const scanlineLayer = document.createElement('div');
+            scanlineLayer.className = 'vhs-scanlines';
+            
+            const glitchLayer = document.createElement('div');
+            glitchLayer.className = 'vhs-glitch';
+            
+            // Insertar al principio del elemento para no afectar los eventos
+            element.insertBefore(glitchLayer, element.firstChild);
+            element.insertBefore(scanlineLayer, element.firstChild);
+            element.insertBefore(noiseLayer, element.firstChild);
+        });
+    }
+    
+    // Añadir viñeta oscura con animación pulsante
+    addDarkVignette() {
+        const vignette = document.createElement('div');
+        vignette.className = 'dark-vignette';
+        this.container.appendChild(vignette);
+        
+        // Animación pulsante sutil
+        setInterval(() => {
+            vignette.classList.toggle('pulse');
+        }, 5000);
+    }
+}
