/**
 * Efectos visuales especialmente diseñados para reforzar la estética gótico-cyberpunk
 * de Cybervania, dándole un carácter único y reconocible
 */
export class GothicEffects {
    constructor(containerId) {
        this.container = document.getElementById(containerId);
        this.elements = {};
        this.initialize();
    }
    
    initialize() {
        this.addGargoyles();
        this.createPentagram();
        this.addBloodSplatter();
        this.setupRitualGlowEffect();
        this.startAmbientLightning();
        
        // Agregar clase especial al título
        const title = document.querySelector('.title-container h1');
        if (title) {
            title.classList.add('gothic-title');
            title.setAttribute('data-text', title.textContent);
        }
    }
    
    addGargoyles() {
        // Añadir gárgolas como vigilantes en las esquinas
        const gargoylePositions = ['top-left', 'top-right', 'bottom-left', 'bottom-right'];
        
        gargoylePositions.forEach((position, index) => {
            const gargoyle = document.createElement('div');
            gargoyle.className = `gargoyle-ornament ${position}`;
            gargoyle.innerHTML = this.getGargoyleSVG(index);
            this.container.appendChild(gargoyle);
            
            // Efectos interactivos en las gárgolas
            gargoyle.addEventListener('mouseenter', () => {
                gargoyle.classList.add('awakened');
                
                // Efecto de ojos brillantes
                const eyes = gargoyle.querySelectorAll('.gargoyle-eye');
                eyes.forEach(eye => {
                    eye.style.fill = '#ff2288';
                    eye.style.filter = 'drop-shadow(0 0 3px #ff2288)';
                });
                
                // Sonido sutil (implementado como console.log por ahora)
                console.log('Gargoyle awakened: subtle stone grinding sound');
            });
            
            gargoyle.addEventListener('mouseleave', () => {
                gargoyle.classList.remove('awakened');
                
                // Devolver ojos a estado normal
                const eyes = gargoyle.querySelectorAll('.gargoyle-eye');
                eyes.forEach(eye => {
                    eye.style.fill = '#8a0303';
                    eye.style.filter = '';
                });
            });
        });
    }
    
    getGargoyleSVG(variant) {
        // Diseños simples de gárgolas con estilo gótico-tech
        const svgTemplates = [
            // Gárgola básica mirando hacia adelante
            `<svg viewBox="0 0 50 50" width="50" height="50">
                <path class="gargoyle-body" d="M25,10 Q35,5 40,15 T25,35 Q15,45 10,30 T25,10" fill="#2d0245" stroke="#bf9b30" stroke-width="0.5"/>
                <circle class="gargoyle-eye" cx="20" cy="18" r="2" fill="#8a0303"/>
                <circle class="gargoyle-eye" cx="30" cy="18" r="2" fill="#8a0303"/>
                <path class="gargoyle-circuit" d="M15,25 h20 M25,15 v15" stroke="#ff2288" stroke-width="0.5" opacity="0.6"/>
            </svg>`,
            
            // Gárgola con alas digitales
            `<svg viewBox="0 0 50 50" width="50" height="50">
                <path class="gargoyle-wing" d="M10,15 Q15,5 25,10 L25,25 Z" fill="#2d0245" stroke="#bf9b30" stroke-width="0.5"/>
                <path class="gargoyle-wing" d="M40,15 Q35,5 25,10 L25,25 Z" fill="#2d0245" stroke="#bf9b30" stroke-width="0.5"/>
                <path class="gargoyle-body" d="M25,10 Q30,15 30,25 T25,35 Q20,40 20,25 T25,10" fill="#2d0245" stroke="#bf9b30" stroke-width="0.5"/>
                <circle class="gargoyle-eye" cx="23" cy="18" r="2" fill="#8a0303"/>
                <circle class="gargoyle-eye" cx="27" cy="18" r="2" fill="#8a0303"/>
                <path class="gargoyle-circuit" d="M15,15 L35,15 M20,20 L30,20" stroke="#ff2288" stroke-width="0.5" opacity="0.6"/>
            </svg>`,
            
            // Gárgola guardián con cuernos
            `<svg viewBox="0 0 50 50" width="50" height="50">
                <path class="gargoyle-horn" d="M20,10 L15,2" stroke="#bf9b30" stroke-width="1"/>
                <path class="gargoyle-horn" d="M30,10 L35,2" stroke="#bf9b30" stroke-width="1"/>
                <path class="gargoyle-body" d="M20,10 Q25,5 30,10 L30,30 Q25,35 20,30 Z" fill="#2d0245" stroke="#bf9b30" stroke-width="0.5"/>
                <circle class="gargoyle-eye" cx="22" cy="15" r="2" fill="#8a0303"/>
                <circle class="gargoyle-eye" cx="28" cy="15" r="2" fill="#8a0303"/>
                <path class="gargoyle-circuit" d="M20,20 L30,20 M20,25 L30,25" stroke="#ff2288" stroke-width="0.5" opacity="0.6"/>
            </svg>`,
            
            // Gárgola tech con elementos mecánicos
            `<svg viewBox="0 0 50 50" width="50" height="50">
                <rect class="gargoyle-body" x="15" y="10" width="20" height="25" rx="5" fill="#2d0245" stroke="#bf9b30" stroke-width="0.5"/>
                <circle class="gargoyle-eye" cx="20" cy="18" r="2" fill="#8a0303"/>
                <circle class="gargoyle-eye" cx="30" cy="18" r="2" fill="#8a0303"/>
                <line x1="15" y1="25" x2="35" y2="25" stroke="#bf9b30" stroke-width="0.5"/>
                <path class="gargoyle-circuit" d="M20,25 v5 h10 v-5 M25,30 v5" stroke="#ff2288" stroke-width="0.5" opacity="0.6"/>
            </svg>`
        ];
        
        return svgTemplates[variant % svgTemplates.length];
    }
    
    createPentagram() {
        // Crear un pentagrama sutil que aparece cuando se carga el menú principal
        const pentagram = document.createElement('div');
        pentagram.className = 'cyber-pentagram';
        pentagram.innerHTML = `
            <svg viewBox="0 0 100 100" width="200" height="200">
                <!-- Pentagrama base con estilo gótico-tech -->
                <path class="pentagram-outline" d="M50,10 L61,40 L93,40 L68,60 L79,90 L50,75 L21,90 L32,60 L7,40 L39,40 Z" 
                      fill="none" stroke="#bf9b30" stroke-width="0.5" opacity="0.3"/>
                
                <!-- Círculo exterior -->
                <circle class="pentagram-circle" cx="50" cy="50" r="45" 
                        fill="none" stroke="#bf9b30" stroke-width="0.3" opacity="0.2"/>
                
                <!-- Símbolos arcanos digitalizados -->
                <text x="48" y="30" font-family="serif" fill="#bf9b30" opacity="0.4" font-size="5">01010</text>
                <text x="73" y="45" font-family="serif" fill="#bf9b30" opacity="0.4" font-size="5">10110</text>
                <text x="65" y="75" font-family="serif" fill="#bf9b30" opacity="0.4" font-size="5">00101</text>
                <text x="35" y="75" font-family="serif" fill="#bf9b30" opacity="0.4" font-size="5">11001</text>
                <text x="26" y="45" font-family="serif" fill="#bf9b30" opacity="0.4" font-size="5">01100</text>
                
                <!-- Circuito interno -->
                <path class="pentagram-circuit" d="M50,10 L50,90 M10,50 L90,50 M25,25 L75,75 M25,75 L75,25" 
                      stroke="#ff2288" stroke-width="0.2" opacity="0.15"/>
            </svg>
        `;
        
        this.container.appendChild(pentagram);
        this.elements.pentagram = pentagram;
        
        // Animar entrada del pentagrama
        setTimeout(() => {
            pentagram.classList.add('reveal');
        }, 1000);
    }
    
    addBloodSplatter() {
        // Añadir pequeñas manchas de sangre digital en los márgenes
        const splatterCount = 5;
        
        for (let i = 0; i < splatterCount; i++) {
            const splatter = document.createElement('div');
            splatter.className = 'blood-splatter';
            
            // Posicionar aleatoriamente en los márgenes
            const side = Math.floor(Math.random() * 4); // 0: top, 1: right, 2: bottom, 3: left
            const position = Math.random() * 80 + 10; // 10% a 90%
            
            switch (side) {
                case 0:
                    splatter.style.top = '10px';
                    splatter.style.left = `${position}%`;
                    break;
                case 1:
                    splatter.style.right = '10px';
                    splatter.style.top = `${position}%`;
                    break;
                case 2:
                    splatter.style.bottom = '10px';
                    splatter.style.left = `${position}%`;
                    break;
                case 3:
                    splatter.style.left = '10px';
                    splatter.style.top = `${position}%`;
                    break;
            }
            
            // Tamaño y rotación aleatorios
            const size = Math.random() * 50 + 20; // 20px a 70px
            const rotation = Math.random() * 360;
            splatter.style.width = `${size}px`;
            splatter.style.height = `${size}px`;
            splatter.style.transform = `rotate(${rotation}deg)`;
            
            // SVG de mancha de sangre digitalizada
            splatter.innerHTML = `
                <svg viewBox="0 0 100 100" width="100%" height="100%">
                    <path d="M50,20 Q70,10 80,30 T60,60 Q40,80 20,50 T50,20" 
                          fill="#8a0303" opacity="0.15" filter="url(#blood-filter-${i})"/>
                    
                    <!-- Filtros para efecto digital -->
                    <defs>
                        <filter id="blood-filter-${i}">
                            <feTurbulence type="fractalNoise" baseFrequency="0.01" numOctaves="1" />
                            <feDisplacementMap in="SourceGraphic" scale="5" />
                        </filter>
                    </defs>
                </svg>
            `;
            
            this.container.appendChild(splatter);
        }
    }
    
    setupRitualGlowEffect() {
        // Efecto de brillo ritual alrededor de los elementos del menú
        const menuScreen = document.querySelector('.menu-screen');
        if (menuScreen) {
            const ritualGlow = document.createElement('div');
            ritualGlow.className = 'ritual-glow';
            
            menuScreen.insertBefore(ritualGlow, menuScreen.firstChild);
            
            // Agregar símbolos arcanos en el fondo
            const arcaneSymbols = document.createElement('div');
            arcaneSymbols.className = 'arcane-symbols';
            arcaneSymbols.innerHTML = this.generateArcaneSymbols();
            
            menuScreen.insertBefore(arcaneSymbols, menuScreen.firstChild);
        }
    }
    
    generateArcaneSymbols() {
        // Generar símbolos arcanos/alquímicos con estilo digital
        let symbols = '';
        const symbolCount = 8;
        const arcaneShapes = [
            'M10,0 L20,20 L0,20 Z', // Triángulo
            'M0,0 L20,0 L20,20 L0,20 Z', // Cuadrado
            'M10,0 L20,10 L10,20 L0,10 Z', // Rombo
            'M10,0 A10,10 0 1,0 10,20 A10,10 0 1,0 10,0 Z' // Círculo
        ];
        
        const arcaneLines = [
            'M5,10 L15,10', // Horizontal
            'M10,5 L10,15', // Vertical
            'M5,5 L15,15', // Diagonal
            'M5,15 L15,5', // Diagonal inversa
            'M10,5 Q15,10 10,15', // Curva
            'M5,10 Q10,5 15,10', // Curva
        ];
        
        for (let i = 0; i < symbolCount; i++) {
            // Posición aleatoria
            const x = Math.floor(Math.random() * 80) + 5; // 5% a 85%
            const y = Math.floor(Math.random() * 80) + 5; // 5% a 85%
            
            // Tamaño y opacidad aleatorios
            const size = Math.floor(Math.random() * 20) + 10; // 10px a 30px
            const opacity = (Math.random() * 0.2 + 0.1).toFixed(2); // 0.1 a 0.3
            
            // Seleccionar forma y líneas aleatorias
            const shape = arcaneShapes[Math.floor(Math.random() * arcaneShapes.length)];
            const line1 = arcaneLines[Math.floor(Math.random() * arcaneLines.length)];
            const line2 = arcaneLines[Math.floor(Math.random() * arcaneLines.length)];
            
            // Crear SVG
            symbols += `
